import React from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import Footer from '../../layout/footer';
import Navbar from '../../layout/navbar';
import './add.scss'
import axios from 'axios';

const Add = () => {
  return (
    <>
      <Navbar />
      <div className='add-formik'>
        <div className='formik-inner'>
          <Formik
            initialValues={{ firstName: '', lastName: '', description: '' }}
            validationSchema={Yup.object({
              firstName: Yup.string()
                .max(25, 'Must be 15 characters or less')
                .required('Required'),
              lastName: Yup.string()
                .max(0, 'Must be 20 characters or less')
                .required('Required'),
                description: Yup.string().required('Required'),
            })}
            onSubmit={(values) => {
              axios.post('http://localhost:3030/',values)
            }}
          >
            <Form>
              <label htmlFor="firstName">ImgUrl</label>
              <Field name="firstName" type="text" />
              <ErrorMessage name="firstName" />

              <label htmlFor="title">Title</label>
              <Field name="title" type="text" />
              <ErrorMessage name="title" />

              <label htmlFor="description">Description</label>
              <Field name="description" type="text" />
              <ErrorMessage name="description" />

              <button type="submit">Submit</button>
            </Form>
          </Formik>
        </div>
      </div>

      <Footer />
    </>
  );
};

export default Add
